// src/components/Header.jsx
import "../styles/main.css";

const scrollToSection = (id) => {
  const section = document.getElementById(id);
  if (section) {
    section.scrollIntoView({ behavior: "smooth" });
  }
};

const Header = () => (
    <header className="header" id="home">
      <div className="header-content">
      <h1>Hi, I'm Karun Gopal</h1>
      <p className="intro">
      Software Engineering Intern @ Principal • CS + Math @ OSU • Full-Stack Dev & AI Enthusiast
      </p>
      <p>
      I build scalable systems, explore AI, and enjoy tackling real-world problems with code.
      </p>
      {/* <a href="#projects" className="cta-button">View My Projects</a> */}
      <button
              onClick={() => scrollToSection("projects")}
              className="cta-button"
              style={{ background: "none", border: "black", cursor: "pointer" }}
            >
              View My Projects
            </button>

      </div>
    </header>
  );

  export default Header;
